import React, { useState, useContext } from "react";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import { green } from "@material-ui/core/colors";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import { Link } from "react-router-dom";
import axios from "axios";
import { UserCookie } from "../hooks/UserCookie";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    marginTop: "2rem",
  },

  formContainer: {
    borderRadius: "8px",
    width: "70%",
    paddingTop: "1rem",
    paddingBottom: "1rem",
    boxShadow: "1px 2px 2px 2px lightgrey",
  },

  field: {
    margin: "1.5rem",
  },

  title: {
    display: "flex",
    marginLeft: "2.5rem",
  },

  bioField: {
    width: "90%",
  },

  photoBtn: {
    color: green[400],
  },

  submitBtn: {
    backgroundColor: green[400],
    color: "white",
    marginTop: "2rem",
  },
}));

export default function SignUp() {
  const { setCookie, state, setState } = useContext(UserCookie);
  const [user, setUser] = useState({
    first_name: "",
    last_name: "",
    email: "",
    phone: 0,
    password: "",
    province: "",
    city: "",
    address: "",
    bio: "",
    education: "",
    avatar: "",
  });

  const createUser = (user) => {
    return axios.put(`/api/users`, user).then((res) => {
      const users = [...state.users, user];
      setState({...state, users});
      setCookie((prev) => ({ ...prev, ...res.data }));
    });
  };

  const classes = useStyles();
  return (
    <Grid container className={classes.root}>
      <h1 style={{ borderBottom: "2px solid #43a047", width: "40%" }}>
        Create Profile
      </h1>
      <Grid item xs={12} className={classes.formContainer}>
        <form
          autoComplete="off"
          onSubmit={(event) => {
            event.preventDefault();
            createUser(user);
          }}
        >
          <div className={classes.title}>
            <h3>Personal Information</h3>
          </div>
          <TextField
            className={classes.field}
            required
            label="First Name"
            type="text"
            placeholder="First Name"
            onInput={(e) =>
              setUser({
                ...user,
                first_name: e.target.value,
              })
            }
            variant="outlined"
            InputLabelProps={{
              shrink: true,
            }}
          />
          <TextField
            className={classes.field}
            required
            label="Last Name"
            type="text"
            placeholder="Last Name"
            variant="outlined"
            onInput={(e) =>
              setUser({
                ...user,
                last_name: e.target.value,
              })
            }
            InputLabelProps={{
              shrink: true,
            }}
          />
          <TextField
            className={classes.field}
            required
            label="Email"
            type="text"
            placeholder="Email"
            variant="outlined"
            onInput={(e) =>
              setUser({
                ...user,
                email: e.target.value,
              })
            }
            InputLabelProps={{
              shrink: true,
            }}
          />
          <TextField
            className={classes.field}
            required
            label="Phone Number"
            type="number"
            placeholder="Phone Number"
            variant="outlined"
            onInput={(e) =>
              setUser({
                ...user,
                phone: e.target.value,
              })
            }
            InputLabelProps={{
              shrink: true,
            }}
          />
          <TextField
            className={classes.field}
            required
            label="Password"
            type="password"
            placeholder="Password"
            variant="outlined"
            onInput={(e) =>
              setUser({
                ...user,
                password: e.target.value,
              })
            }
            InputLabelProps={{
              shrink: true,
            }}
          />
          <TextField
            className={classes.field}
            required
            label="Confirm Password"
            type="password"
            placeholder="Confirm Password"
            variant="outlined"
            InputLabelProps={{
              shrink: true,
            }}
          />
          <TextField
            className={classes.field}
            required
            label="Province"
            type="text"
            placeholder="Province"
            variant="outlined"
            onInput={(e) =>
              setUser({
                ...user,
                province: e.target.value,
              })
            }
            InputLabelProps={{
              shrink: true,
            }}
          />
          <TextField
            className={classes.field}
            required
            label="City"
            type="text"
            placeholder="City"
            variant="outlined"
            onInput={(e) =>
              setUser({
                ...user,
                city: e.target.value,
              })
            }
            InputLabelProps={{
              shrink: true,
            }}
          />
          <TextField
            className={classes.field}
            required
            label="Address"
            type="text"
            placeholder="Address"
            variant="outlined"
            onInput={(e) =>
              setUser({
                ...user,
                address: e.target.value,
              })
            }
            InputLabelProps={{
              shrink: true,
            }}
          />

          <br />
          <br />
          <div className={classes.title}>
            <h3>Bio</h3>
          </div>
          <TextField
            className={classes.bioField}
            id="outlined-multiline-static"
            label="Tell us about yourself"
            multiline
            rows={4}
            variant="outlined"
            fullWidth
            onInput={(e) =>
              setUser({
                ...user,
                bio: e.target.value,
              })
            }
          />
          <br />
          <br />
          <div className={classes.title}>
            <h3>Education and Certifications</h3>
          </div>
          <TextField
            className={classes.bioField}
            id="outlined-multiline-static"
            label="Share some of your qualifications"
            multiline
            rows={2}
            variant="outlined"
            fullWidth
            onInput={(e) =>
              setUser({
                ...user,
                education: e.target.value,
              })
            }
          />
          <br />
          <br />
          <div className={classes.title}>
            <h3>Profile Photo</h3>
          </div>
          <TextField
            className={classes.bioField}
            id="outlined-multiline-static"
            label="Image URL"
            multiline
            rows={1}
            fullWidth
            variant="outlined"
            onInput={(e) =>
              setUser({
                ...user,
                avatar: e.target.value,
              })
            }
          />
          <Button
            type="submit"
            className={classes.submitBtn}
            size="large"
            variant="contained"
          >
            Submit
          </Button>
        </form>
      </Grid>
    </Grid>
  );
}
