import React from 'react';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({

  root: {
  },

  message: {

  },

  sentContainer: {
    backgroundColor: '#0EE290'
  },

  sentText: {
    color: 'white'
  },

  receivedContainer: {
    backgroundColor: '#EFEFEF'
  },

  receivedText: {
    color: 'black'
  }

}));


export default function Message(props) {

  const classes = useStyles();

  // console.log('props', props);
  console.log('this is the user in the message component', props.sender_id);

  return(
    props.user === props.userID || props.sender_id === props.userID ? (
      <div className={classes.root}>
        <div className={`${classes.message} ${classes.sent}`}>
          <p>{props.text}</p>
        </div>
      </div>
    ) : 
    (
    <div className={classes.root}>
      <div className={classes.received}>
        <p>{props.text}</p>
      </div>
    </div>
    )

  )

}
